command:
/* Author: n0q
 *
 * About: Generates potential character stats, and notes arrays
 * which hit the Shopkeeper Rule as per Hackmaster PHB10.
 */

const abilities = ['STR', 'INT', 'WIS', 'DEX', 'CON', 'LKS', 'CHA'];
const rolls = await Promise.all(abilities.map(async (a) => ({
    value: Number((await new Roll('3d6').evaluate()).result),
    fvalue: Number((await new Roll('d100').evaluate()).result),
})));

const statData = {
    sum: 0.00,
    bp: 0,
    get avg() { return this.sum / 7 }, 
    _addBP: function(stat) {
        let value = -10; // Minimum attribute score is 1/01.
        if (stat < 10) { value += ((stat -  0) / 0.10) + 0;   } else
        if (stat < 16) { value += ((stat - 10) / 0.05) + 100; } else
                       { value += ((stat - 16) / 0.03) + 220; };
        this.bp += Math.ceil(value);
    },
};

const skData = {high: false, low: 0};
const rollData = abilities.reduce((acc, key, idx) => {
    const result = rolls[idx];
    const sum = result.value + (result.fvalue * 0.01) - 0.01;
    statData.sum += sum;
    statData._addBP(sum);
    if (result.value > 12) { skData.high = true; } else
    if (result.value < 6)  { skData.low++; }
    acc[key] = {value: result.value, fvalue: result.fvalue % 100};
    return acc;
}, {});

const isShopkeeper = !skData.high || skData.low > 1;
const data = {isShopkeeper, rollData, statData};

const hbs = `
  <section style='position: relative;'>
  {{#if isShopkeeper}}
    <div style='position: absolute;
                top: 50%;
                left: 50%;
                transform: translateX(-50%) translateY(-50%) rotate(-33deg);
                transform-origin: center;
                font-size: 24px;
                color: rgba(0, 0, 0, 0.35);'>
    Shopkeeper</div>
  {{/if}}
    <table><tbody>
  {{#each rollData}}
      <tr>
        <th scope=row>{{@key}}</th>
        <td style='text-align: right;'>
          {{value}}<b>&frasl;</b>{{pad fvalue}}
        </td>
      </tr>
  {{/each}}
    </tbody></table>
  </section>
  <section style='display: flex; justify-content: space-between;'>
  {{#with statData}}
    <span>sum: <b>{{numberFormat sum decimals=2}}</b></span>
    <span><b>{{bp}} BP</b></span>
    <span>avg: <b>{{numberFormat avg decimals=2}}</b></span>
  {{/with}}
  </section>`;

const cmData = {
    content: (Handlebars.compile(hbs))(data),
    flavor: 'Ability Score Roll',
    sound: CONFIG.sounds.dice,
};
const msgData = await new ChatMessage(cmData);
ChatMessage.create(msgData);
